import Vue, { PluginObject } from 'vue'
 
declare module 'vue-loading-overlay' {
  export interface OverlayLoader {
    hide: () => void,
    isActive: boolean
  }
  const Loading: PluginObject<any>
  export default Loading
}

declare module 'vue/types/vue' {
  interface Vue {
    $loading: {
      show: (props?: {
        container: any,
        canCancel: boolean,
        onCancel: Function,
        color: string,
        loader: string,
        width: number,
        height: number,
        backgroundColor: string,
        opacity: number,
        zIndex: number,
      }, slots?: {
        default: any
      }) => OverlayLoader
    }
  }
}